mapper.select:
select t1.id, t1.house_id, DATE_FORMAT(t1.start_date,'%Y-%m-%d') as start_date, DATE_FORMAT(t1.end_date,'%Y-%m-%d') as end_date, t2.name, t2.location, t3.name as landlordName, t3.phone as landlordPhone, t1.state, t1.amount_actual, t1.create_time from (select * from client_user_order where user_id=#{userId} and is_del_user=0) as t1 inner join client_house as t2 on t1.house_id=t2.id inner join client_user as t3 on t3.id=t2.user_id
and t2.`name` like concat("%",#{houseName},"%")
and t2.tree_code = #{treeCode}
and t1.state = #{state}
order by t1.create_time desc limit #{sqlPage},#{limit}

select count(t1.id) from (select * from client_user_order where user_id=#{userId} and is_del_user=0) as t1
inner join client_house as t2 on t1.house_id=t2.id

    and t2.`name` like concat("%",#{houseName},"%")


    and t2.tree_code = #{treeCode}


    and t1.state = #{state}

select t2.id, t2.house_id, DATE_FORMAT(t2.start_date,'%Y-%m-%d') as start_date, DATE_FORMAT(t2.end_date,'%Y-%m-%d') as end_date, t1.name, t1.location, t3.name as userName, t3.phone as userPhone, t2.state, t2.amount_actual, t2.create_time from (select * from client_house where user_id=#{userId}) as t1 inner join client_user_order as t2 on t2.house_id=t1.id inner join client_user as t3 on t3.id=t2.user_id where t2.is_del_landlord=0
and t1.`name` like like concat("%",#{houseName},"%")
and t1.tree_code = #{treeCode}
and t2.state = #{state}
order by t2.create_time desc limit #{sqlPage},#{limit}

select count(t1.id) from (select * from client_house where user_id=#{userId}) as t1 inner join client_user_order as t2 on t2.house_id=t1.id inner join client_user as t3 on t3.id=t2.user_id where t2.is_del_landlord=0
and t1.`name` like concat("%",#{houseName},"%")
and t1.tree_code = #{treeCode}
and t2.state = #{state}
